mapper.select:
SELECT a.id AS agencyId, a.name AS agencyName, c.base_score AS baseScore, c.job_score AS jobScore, c.practice_score AS practiceScore, c.education_score AS educationScore, c.work_experience_score AS workExperienceScore, c.positional_info_score AS positionalInfoScore, c.science_education_score AS scienceEducationScore, c.reward_info_score AS rewardInfoScore, c.cultivate_score AS cultivateScore, c.year_assess_score AS yearAssessScore, c.sum_score AS sumScore, c.update_time AS updateTime FROM org_agency a LEFT JOIN analysis_agency_data_score c ON c.agency_id = a.id WHERE a.is_delete = 0
AND a.city_code =#{permission.cityCode}
AND a.city_code IN
#{item}
AND a.town_code =#{permission.townCode}
AND a.town_code IN
#{item}
AND a.district_code =#{permission.districtCode}
AND a.district_code IN
#{item}
AND a.id =#{permission.agencyId}
AND a.id IN
#{item}
