SELECT j.`id` AS id, p.`name` AS NAME, p.`sex` AS sex, p.`agency_name` AS agencyName, j.`positional_title_name` AS positionalTitleName, J.`positional_title_type` AS positionalTitleType, c.`status` AS STATUS FROM org_personnel_positional_info AS j LEFT JOIN org_personnel AS p ON p.id=j.`personnel_id` LEFT JOIN complaint_positional_title AS c ON j.id=c.id WHERE p.is_delete = 0
AND P.city_code =#{permission.cityCode}
AND P.city_code IN
#{item}
AND P.town_code =#{permission.townCode}
AND P.town_code IN
#{item}
AND P.district_code =#{permission.districtCode}
AND P.district_code IN
#{item}
AND P.agency_id =#{permission.agencyId}
AND P.agency_id IN
#{item}
AND P.name LIKE CONCAT('%',#{query.name},'%')
AND P.agency_name LIKE CONCAT('%',#{query.agencyName},'%')

